function captureDisplay(_type, _keyCode)
{
	if(_type == "keyup")
	{
		//$('#notifier').text("This is the test.");
		$('#focus2').focus();
		$('#focus2').addClass('keypressUP');
		$('#focus1').removeClass();
		
		if(_keyCode == 90) /* Shift */
		{
			$('#shift').addClass('keypressUP');
			$('#shift').removeClass('keypressDOWN');
			
			if($('#cmd').hasClass('keypressDOWN') == true || $('#wincmd').hasClass('keypressDOWN') == true)
			{
				$('#notifier').text("Meta Key UP.");
				$('#politenotifier').text("This is some polite text.");
			}
			
		}
